import { Firestore, FieldValue } from '@google-cloud/firestore';

const firestore = new Firestore(undefined);

const RoadmapItem = {
  fromFirestore: (snapshot) => {
    const data = snapshot.data();
    console.log(data);
    return {
      id: snapshot.ref.id,
      feature: data.feature,
      description: data.description,
      status: data.status,
      targetRelease: data.targetRelease ? data.targetRelease.toDate ? data.targetRelease.toDate() : undefined : undefined,
      votesSummary: data.votesSummary,
      _createdBy: data._createdBy,
      _updatedBy: data._updatedBy
    }
  },
}

const Vote = {
  fromFirestore: (snapshot) => {
    const data = snapshot.data();
    return {
      vote: data.vote,
      comment: data.comment,
      email: data.email,
    }
  }
}

const TimelogItem = {
  fromFirestore: (snapshot) => {
    const data = snapshot.data();
    return {
      actor: data.actor,
      changedFields: data.changedFields
    }
  }
}

async function getSchema({ tableId, collection, name }) {
  const settingsRef = firestore.collection("_rowy_").doc("settings");
  const schemaRef = settingsRef.collection("schema").doc(tableId);
  const votesSchemaRef = schemaRef.collection("subTables").doc("votes");

  const schemaSnapshot = await schemaRef.get();
  if(schemaSnapshot.exists) {
    return schemaSnapshot.data();
  }
  const settings = tableSettings({ tableId, collection, name });
  console.log("settings: ", settings);
  await Promise.all([
    settingsRef.update({ tables: FieldValue.arrayUnion(settings) }, { merge: true }),
    schemaRef.set(roadmapSchema),
    votesSchemaRef.set(votesSchema)
  ])
  return roadmapSchema;
}

async function getStatusOptions({ tableId, collection, name }) {
  const schema = await getSchema({ tableId, collection, name });
  return schema.columns.status.config.options;
}

async function listItems({ status, collection }) {
  const itemsRef = status ? 
    firestore.collection(collection).where("status", "==", status) : 
    firestore.collection(collection);

  const snapshot = await itemsRef.get();
  return snapshot.docs.map(RoadmapItem.fromFirestore);
}

async function getItem({ itemId, collection }) {
  const itemRef = firestore.collection(collection).doc(itemId);
  const votesRef = firestore.collection(collection).doc(itemId).collection("votes");
  const [itemSnapshot, votesSnapshot] = await Promise.all([itemRef.get(), votesRef.get()]);

  const item = RoadmapItem.fromFirestore(itemSnapshot);
  const votes = votesSnapshot.docs.map(Vote.fromFirestore);
  return { ...item, votes }
}

async function createVote({ collection, itemId, vote }) {
  const itemRef = firestore.collection(collection).doc(itemId);
  const votesRef = firestore.collection(collection).doc(itemId).collection("votes");

  return Promise.all([
    votesRef.add({
      vote: vote.vote,
      comment: vote.comment,
      email: vote.email,
    }),
    itemRef.update({
      [`votesSummary.${vote.vote}`]: FieldValue.increment(1),
    })
  ]);
}

export default async ({ tableId, collection, name },{ logging, request }) => {
  let data;
  try {
    const { method, options } = request.body;
    switch(method) {
      case "getStatusOptions":
        data = await getStatusOptions({ tableId, collection, name });
        console.log("data: ", data);
        return { success: true, data };
      case "listItems":
        data = await listItems({ collection, status: options.status });
        return { success: true, data };
      case "getItem":
        data = await getItem({ collection, itemId: options.itemId });
        return { success: true, data };
      case "createVote":
        await createVote({ collection, itemId: options.itemId, vote: options.vote });
        return { success: true }
        break;
      default:
        return { success: false, error: "Unsupported method" };
    }
  } catch(error) {
    return { success: false, error }
  }
}

